import React from "react";
import styles from './inputForm.module.css'
import {CSSTransition} from "react-transition-group";


export function InputForm({handleChange, contact, number}) {
    return (
        <div>
            <CSSTransition in={true} timeout={20000} classNames="fade">
                <h2 className={styles.appear}>Phonebook</h2>
            </CSSTransition>
            <h3 className={styles.title}>Name</h3>
            <input
                name='contact'
                placeholder="Please put the name"
                onChange={handleChange}
                value={contact}
                type="text"
            />
            <h3 className={styles.subtitle}>Number</h3>
            <input
                name='number'
                placeholder="Please put the number"
                onChange={handleChange}
                value={number}
                type="number"
            />
        </div>
    )
}
