import React from 'react';
// import ListItem from "../ListItem/ListItem";
import InputSearch from "../InputForm/InputSearch";
import styles from './list.module.css';
import {TransitionGroup, CSSTransition} from "react-transition-group";
import popTransition from "./transitions/pop.module.css"

const List = ({contacts, filterList, filter, getFilterValue, deleteItem}) => {
    return (
        <>
            {contacts.length >= 2 ?
                <>
                    <h3 className={styles.title}>Contacts</h3>
                    <InputSearch
                        filter={filter}
                        getFilterValue={getFilterValue}
                    />
                </>
                : <></>}
            <TransitionGroup component='ul'>
                {filterList.map(contact =>
                        <li>
                            <p className={styles.sign}>{contact.contact} : {contact.number}</p>
                            <button className={styles.btn} id={contact.id}
                                    onClick={deleteItem}
                            >delete</button>
                        </li>
                    // <CSSTransition timeout={200}>
                    // //                classNames={popTransition}
                    // //                unmountOnExit>
                    //     <ListItem key={contact.id}
                    //               deleteItem={deleteItem}
                    //               contact={contact}/>)}
                    // // </CSSTransition>
                )}
            </TransitionGroup>
        </>
    );


}
export default List;
