import React, {Component} from 'react';
import ListItem from "../ListItem/ListItem";
import InputSearch from "../InputForm/InputSearch";
import styles from './list.module.css';
import {TransitionGroup, CSSTransition} from "react-transition-group";
import popTransition from "./transitions/pop.module.css"
import searchTransition from './transitions/search.module.css'

class List extends Component {
    state = {
        inSearch: false
    }

    componentDidUpdate=()=>{
        this.setState({inSearch:true})
    }


    render() {
        console.log(this.state.inSearch)
        return (
            <>
                {this.props.contacts.length >= 2 ? this.setState({inSearch:true})&&
                    <>
                        <CSSTransition in={this.state.inSearch} timeout={250} classNames={searchTransition} unmountOnExit>

                            <h3 className={styles.title}>Contacts</h3>
                            <InputSearch
                                filter={this.props.filter}
                                getFilterValue={this.props.getFilterValue}
                            />
                        </CSSTransition>
                    </>
                    : <></>}
                <TransitionGroup component='ul'>
                    {this.props.filterList.map(contact =>
                        <CSSTransition timeout={200}
                                       classNames={popTransition}
                                       unmountOnExit>
                            <ListItem key={contact.id}
                                      deleteItem={this.props.deleteItem}
                                      contact={contact}/>
                        </CSSTransition>
                    )}
                </TransitionGroup>
            </>

        )
    }


}

export default List;


